import React, { useState } from 'react';
import { Card, Col, Row, Input, List, Button, Typography, Cascader, Select, Radio, Divider, Avatar } from 'antd';
import AppLayout from './components/AppLayout';
import { useSelector, useDispatch } from 'react-redux';
import Link from 'next/link';

const dummy = {
    nickname:'user1',
    prefer_language:['react-native', 'java', 'c/c#'],
}

const MyPage = () => {
    const [passck, setPassck] = useState('');
    const [term, setTerm] = useState(false);


    return (
        <>
            <div>
                <div  style={{float: 'right', marginRight:'10px'}}>
                    <p>
                    <Link href="/profile"style={{marginRight:'10px'}}><a>MyPage</a></Link>  |  장바구니
                    </p>
                </div>
                <h1 style={{ margin: '15px', paddingTop: '30px'}}>Code-ing</h1>
                <div style={{ textAlign: 'center' }}>
                                <Select
                                style={{
                                    float:'left'}}
                                    showSearch
                                    style={{ width: 100 }}
                                    placeholder="카테고리"
                                    optionFilterProp="children"
                                    onChange={onChange}
                                    onFocus={onFocus}
                                    onBlur={onBlur}
                                    onSearch={onSearch}
                                    filterOption={(input, option) =>
                                        option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                                    }
                                >
                                    <Option value="강의">강의</Option>
                                    <Option value="언어">언어</Option>
                                    {/* <Option value="tom">Tom</Option> */}
                                </Select>
                                <Select
                                    mode="multiple"
                                    style={{ width: "200px" }}
                                    placeholder="검색"
                                    defaultValue={[]}
                                    onChange={handlePeopleChange}
                                >
                                    {people}
                                </Select>
                                <Button style={{ marginTop: '20px' }}>검색</Button></div>
            </div>
            <AppLayout>
                <Row gutter={10}>
                    <Col xs={3} md={5}>
                    </Col>
                    <Col xs={18} md={14}>
                       <Row style={{marginTop:'10px'}}>
                           <Card style={{border:"0px solid"}}>
                                <Card.Meta
                                    title={dummy.nickname}
                                    avatar={<Avatar>{dummy.nickname[0]}</Avatar>}
                                    description={'#'+dummy.prefer_language[0]+', #'+dummy.prefer_language[1]}
                                    style={{ height: '50px' }}
                                />
                                <div style={{float:'right'}}>
                                    <Link href=''>개인정보 수정</Link>
                                </div>   
                           </Card>
                        </Row>
                        <Row style={{marginTop:'10px'}}>
                            <List
                                grid={{gutter: 16}}
                                dataSource={[{ name: '즐거찾기한 강의', url: '/myFavoriteLectures'},
                                            { name: '즐겨찾기한 사이트', url: '/myFavoritesSites'},
                                            { name: '내가 쓴 리뷰', url: '/myreview'},
                                            { name: '내가 쓴 글', url: '/mypost'},
                                            { name: '댓글 단 글', url: '/mycomment'}
                                        ]}
                                renderItem={item => (
                                    <List.Item >
                                        <Link href={item.url}>
                                            <Card style={{border:"1px solid"}}>
                                                <Card.Meta
                                                    title={item.name}
                                                    style={{ height: '10px'}}
                                                />
                                            </Card>
                                        </Link>
                                    </List.Item>

                                )}
                            />
                        </Row>
                    </Col>
                    <Col xs={3} md={5}>
                    </Col>
                </Row>
            </AppLayout>
        </>
    );
};

export default MyPage;
